import { useState } from "react";
import axios from "axios";

const ChatInput = ({ userId, clickedUserId }) => {
    const [message, setMessage] = useState("");

    const sendMessage = async (e) => {
        e.preventDefault();
        if (!message) return; // Ako nema poruke, ne šaljemo

        const messageData = {
            from_userId: userId,
            to_userId: clickedUserId,
            text: message,
            timestamp: new Date().toISOString(),
        };

        // Šaljemo poruku putem soketa
        socket.emit('send_message', messageData);

        // Također možemo pohraniti poruku u bazu
        try {
            await axios.post('http://localhost:8000/message', { message: messageData });
        } catch (error) {
            console.error("Error sending message:", error);
        }

        // Očisti unos poruke
        setMessage("");
    };

    return (
        <form onSubmit={sendMessage}>
            <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Type your message..."
            />
            <button type="submit">Send</button>
        </form>
    );
};

export default ChatInput;
