import React from 'react';
import Home from './pages/Home';
import Dashboard from './pages/Dashboard';
import Onboarding from './pages/Onboarding';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { useCookies } from 'react-cookie';
import { SocketProvider } from './SocketContext'; // Adjust the path as necessary

const App = () => {
  const [cookies, setCookie, removeCookie] = useCookies(['user']);
  const authToken = cookies.AuthToken;

  const handleLogout = () => {
    removeCookie('AuthToken');
    // Optionally redirect to home or login page
    window.location.href = '/'; // You can also use a navigate function if you prefer
  };

  return (
    <SocketProvider>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Home />} />
          {authToken ? (
            <>
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/onboarding" element={<Onboarding />} />
            </>
          ) : (
            <>
              {/* Redirect unauthorized users */}
              <Route path="/dashboard" element={<Navigate to="/" />} />
              <Route path="/onboarding" element={<Navigate to="/" />} />
            </>
          )}
        </Routes>
        {/* Example logout button */}
        {authToken && <button onClick={handleLogout}>Logout</button>}
      </BrowserRouter>
    </SocketProvider>
  );
};

export default App;
