import ChatDisplay from './ChatDisplay';
import ChatInput from './ChatInput';

const Chat = ({ userId, correspondingUserId, descendingOrderMessages }) => {
    return (
        <>
            <div className="chat-display">
                <ChatDisplay userId={userId} correspondingUserId={correspondingUserId} />
            </div>
            <ChatInput userId={userId} correspondingUserId={correspondingUserId} />
        </>
    );
};

export default Chat;
